export default {
  name: "comment",
  data() {
    return {
      gitalk: null
    };
  },
  mounted() {
    this.pageInit();
  },
  methods: {
    pageInit() {
      console.log("this", this);
      const url = location.href;
      let arr = ["http://obkoro1.com/web_accumulate/about.html"];
      // 过滤某些页面
      if (!arr.includes(url)) {
        this.initGitalk();
        this.initIssue();
      }
    },
    // 使用油候初始化issue
    initIssue() {
      let val = sessionStorage.pageArray;
      if (!val) {
        let pageArray = this.$site.pages;
        pageArray = pageArray.map(item => {
          return `${location.origin}/web_accumulate${decodeURI(item.path)}`;
        });
        sessionStorage.setItem("pageArray", JSON.stringify(pageArray));
      }
    },
    issueTitle() {
      const title = '前端进阶积累';
      const pathArr = location.pathname.split("/");
      let res;
      let articleTile = this.$page.title;
      if (pathArr[3]) {
        // 文章页面创建issue
        if (pathArr[2] === "algorithm") {
          // 算法
          let obj = {
            induction: "入门级-算法",
            simple: "简单级-算法",
            medium: "中等级-算法"
          };
          res = `${articleTile} | ${obj[pathArr[3]]}`;
        } else if(pathArr[2] === 'codeBlack'){
          res = `${articleTile} | 开箱即用的代码块`;
        }else {
          // 文章
          res = `${articleTile} | ${pathArr[3]}`;
        }
      } else if (pathArr[2]) {
        // 第二层主目录是否创建issue
        let agreeArr = ["accumulate", "algorithm", 'codeBlack'];
        if (agreeArr.includes(pathArr[2])) {
          let obj = {
            accumulate: "前端进阶积累",
            algorithm: "前端算法",
            codeBlack: '开箱即用的代码块'
          };
          res = obj[pathArr[2]];
        }
      }
      return [res, articleTile];
    },
    issueLabels() {
      // 切割超过50个字符的标签 避免创建不了issue
      let labels = this.$page.headers.map(item => {
        if (item.title.length > 50) {
          item.title = item.title.slice(0, 48);
        }
        return item.title;
      });
      // 过滤一些标签
      labels = labels.filter(item => {
        return !["鼓励我一下：", "小结"].includes(item);
      });
      // 限制标签数
      if (labels.length > 10) {
        labels.length = 10;
      }
      // 修改头部的meta
      document.querySelector("meta[name=description]").content = labels.join(
        ","
      );
      return labels;
    },
    initGitalk() {
      let body = document.querySelector(".gitalk-container");
      let script = document.createElement("script");
      script.src = "https://cdn.jsdelivr.net/npm/gitalk@1/dist/gitalk.min.js";
      body.appendChild(script);
      script.onload = () => {
        let val = sessionStorage.gitalkOBKoro1;
        if (val && val === "issue失败") {
          this.newGitalk(false);
        } else {
          this.newGitalk();
        }
      };
    },
    // 根据链接获取仓库
    getRepo() {
      const url = location.href;
      const obj = {
        "/web_accumulate/algorithm/": "Brush_algorithm", // 算法仓库
        "/web_accumulate/accumulate/": "web_accumulate", // 博客仓库
        "/web_accumulate/codeBlack/": "codeBlack"
      };
      let repoName = obj["/web_accumulate/accumulate/"]; // 默认博客仓库
      for (const key in obj) {
        const index = url.indexOf(key);
        if (index !== -1) {
          repoName = obj[key];
        }
      }
      return repoName;
    },
    newGitalk(createLabels = true) {
      const [title, articleTile] = this.issueTitle();
      let labels = [articleTile];
      if (createLabels) {
        labels = this.issueLabels();
      }
      console.log('labels',labels)
      let article = this.$page.excerpt.replace(
        new RegExp('<div class="line-numbers-wrapper">.*?<\\/div>', "g"),
        ""
      );
      let body = `### [博客链接](${location.href})\n${article}\n [博客链接](${location.href})`;
      if (title) {
        const commentConfig = {
          clientID: "8fbce2735aa4b865e9df",
          clientSecret: "c2d2947de913af238dc5a22b1db8de0d9e834096",
          repo: this.getRepo(), // github项目名
          owner: "OBKoro1",
          // 这里接受一个数组，可以添加多个管理员
          admin: ["OBKoro1"],
          // id 需要用pathname否则将会重复创建。

          // 但是如果你的 pathname 超过 50 个字符，GitHub 将不会成功创建 issue，此情况可以考虑给每个页面生成 hash 值的方法.
          // id: decodeURI(location.pathname).slice(0, 48), // issue title
          id: `web ${articleTile}`,
          title, // issue标题
          body, // issue内容
          labels: labels, // issue标签
          distractionFreeMode: false
        };
        this.errCatch();
        if (this.gitalk) {
          // 更新配置并重新请求
          sessionStorage.setItem("gitalkOBKoro1", "issue失败");
          location.reload();
        } else {
          this.gitalk = new Gitalk(commentConfig);
          this.gitalk.render("gitalk-container");
          sessionStorage.setItem("gitalkOBKoro1", "issue成功");
        }
      }
    },
    // 创建失败
    errCatch() {
      var log = console.log;
      let self = this;
      console.log = function(msg, data) {
        // 拦截issue抛出的错误 刷新页面 labels改值
        if (msg === "err:") {
          if (data.config.baseURL === "https://api.github.com") {
            self.newGitalk(false);
          }
        }
        log.apply(this, Array.prototype.slice.call(arguments));
      };
    }
  }
};
