import file2 from "../../../README.md";

export default {
  name: "comment",
  data() {
    return {};
  },
  methods: {
    issueTitle() {
      console.log("this", this, window, file2);
      const title = location.pathname;
      const pathArr = title.split("/");
      let res;
      // "js 调用栈机制与ES6尾调用优化介绍"
      // "OBKoro1前端积累"
      let articleTile = this.$page.title;
      if (pathArr[3]) {
        // 文章页面创建issue
        if (pathArr[2] === "algorithm") {
          // 算法
          let obj = {
            induction: "入门级-算法",
            simple: "简单级-算法",
            medium: "中等级-算法"
          };
          res = `${articleTile} | ${obj[pathArr[3]]}`;
        } else {
          // 文章
          res = `${articleTile} | ${pathArr[3]}`;
        }
      } else if (pathArr[2]) {
        // 第二层主目录是否创建issue
        let agreeArr = ["accumulate", "algorithm"];
        if (agreeArr.includes(pathArr[2])) {
          let obj = {
            accumulate: "OBKoro1博客",
            algorithm: "前端算法"
          };
          res = obj[pathArr[2]];
        }
      }
      console.log("pathArr", pathArr, res);
      return [res, articleTile];
    },
    issueLabels() {
      let labels = this.$page.headers.map(item => {
        return item.title;
      });
      // 增加meta
      document.querySelector("meta[name=description]").content = labels.join(
        ","
      );
      labels.length = 10;
      labels = [
        "labelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabelslabels",
        ...labels
      ];
      return labels;
    }
  },
  mounted() {
    let body = document.querySelector(".gitalk-container");
    let script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/gitalk@1/dist/gitalk.min.js";
    body.appendChild(script);
    script.onload = () => {
      const [title, articleTile] = this.issueTitle();
      const labels = this.issueLabels();
      // 创建issue TODO: false
      window.num = 0;
      window.num++;
      let body = "# 哈哈哈";
      if (window.num === 2) {
        body = "<h2>h2h2 哈哈哈</h2>";
      }
      if (title) {
        const commentConfig = {
          clientID: "8fbce2735aa4b865e9df",
          clientSecret: "c2d2947de913af238dc5a22b1db8de0d9e834096",
          repo: "web_accumulate",
          owner: "OBKoro1",
          // 这里接受一个数组，可以添加多个管理员
          admin: ["OBKoro1"],
          // id 用于当前页面的唯一标识，一般来讲 pathname 足够了，

          // 但是如果你的 pathname 超过 50 个字符，GitHub 将不会成功创建 issue，此情况可以考虑给每个页面生成 hash 值的方法.
          id: articleTile, // issue title
          title,
          body,
          // labels: labels,
          distractionFreeMode: false
        };
        const gitalk = new Gitalk(commentConfig);
        gitalk.render("gitalk-container");
        console.log('document.querySelector(".gt-copyright")', commentConfig);
      }
    };
  }
};
